import { useState ,useEffect } from 'react';
import NumberFormat from 'react-number-format';
import './App.css';



function App() {
  const [preState, setPreState] = useState("");
  const [curState, setCureState] = useState("");
  const [input, setInput] = useState(0);
  const [operation, setOperation] = useState(null);
  const [total, setTotal] = useState(false);

  const inputNum = (e) => {
    if (total) {
      setPreState("");
    }
    curState ? setCureState((pre) => pre + e.target.innerText) : setCureState(e.target.innerText);
    setTotal(false)
  };
  useEffect(() => {
    setInput(curState)
  }, [curState])
  useEffect(( )=> {
    setInput("0")
  },[])
  const operatorType = (e) => {
    setTotal(false);
    setOperation(e.target.innerText)
    if (curState === "") return
    if (preState !== "") {
      equal();
    }
    setPreState(curState);
    setCureState("");
    let result;
    switch (operation) {

  case "+":
    result = String(parseFloat(preState) + parseFloat(curState));
    break;
  case "-":
    result = String(parseFloat(preState) - parseFloat(curState));
    break;
  case "*":
    result = String(parseFloat(preState) * parseFloat(curState));
    break;
  case "/":
    result = String(parseFloat(preState) / parseFloat(curState));
    break;

  default:
    }
    setPreState(result)
    setCureState("");
    setInput(result);
  } 
  const equal = (e) => {
    if (e.target.innerText === "=") {
      setTotal(true);
    }
  }



  const addAdiff = (e) => {

  }
  const percent = (e) => {

  }
  const reset = (e) => {
    setPreState("");
    setCureState("");
    setInput("0");
  }

  return (
    <div className="App">
      <div className='container'>
        <div className='container-content'>
          <div className='screen'>  {input !=="" || input ==="0" ? <NumberFormat value={input} displayType={'text'} thousandSeparator={true} /> : <NumberFormat value={preState} displayType={'text'} thousandSeparator={true} />}        </div>
            <div className='btn light'  onClick={reset}>AC</div>
            <div className='btn light' onClick={percent}>%</div>
            <div className='btn light' onClick={addAdiff}>+/-</div>
            <div className='btn orange' onClick={operatorType}>/</div>
            <div className='btn siven' onClick={inputNum}>7</div>
            <div className='btn eight' onClick={inputNum}>8</div>
            <div className='btn nine' onClick={inputNum}>9</div>
            <div className='btn orange' onClick={operatorType}>x</div>
            <div className='btn four' onClick={inputNum}>4</div>
            <div className='btn five' onClick={inputNum}>5</div>
            <div className='btn six' onClick={inputNum}>6</div>
            <div className='btn orange' onClick={operatorType}>+</div>
            <div className='btn one' onClick={inputNum}>1</div>
            <div className='btn two' onClick={inputNum}>2</div>
            <div className='btn three' onClick={inputNum}>3</div>
            <div className='btn orange' onClick={operatorType}>-</div>
            <div className='btn zero' onClick={inputNum}>0</div>
            <div className='btn Dot' onClick={inputNum}>.</div>
            <div className='btn equal' onClick={equal}>=</div>

        </div>
      </div>
    </div>
  );
}

export default App;
